mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM board
    where board_id = #{commentBoardId}
      AND board_isactive = 1
) as "checkBoardId"

SELECT EXISTS(
    SELECT 1
    FROM comment
    WHERE comment_board_id = #{commentBoardId}
      AND comment_id = #{commentParentId}
      AND comment_isactive = 1
) as "checkCommentId"
