mapper.insert:
INSERT INTO member (
    member_email, member_pw, member_name, member_nickname,
    member_phone, member_birth, member_gender
) VALUES (
    #{memberEmail}, #{memberPw}, #{memberName}, #{memberNickname},
    #{memberPhone}, #{memberBirth}, #{memberGender}
)

INSERT INTO member (
    member_email, member_pw, member_name, member_nickname,
    member_phone, member_birth, member_gender, member_provider
) VALUES (
     #{memberEmail}, #{memberPw}, #{memberName}, #{memberNickname},
     #{memberPhone}, #{memberBirth}, #{memberGender}, #{memberProvider}
)

mapper.select:
SELECT member_email
FROM member
WHERE member_name = #{memberName}
AND member_phone = #{memberPhone}
